// Clock Component
function Clock() {
    const [isDigital, setIsDigital] = useState(false);
    const [time, setTime] = useState(new Date());

    useEffect(() => {
        const interval = setInterval(() => setTime(new Date()), 1000);
        return () => clearInterval(interval);
    }, []);

    const formatTime = (date) =>
        `${date.getHours().toString().padStart(2, "0")}:${date
            .getMinutes()
            .toString()
            .padStart(2, "0")}:${date.getSeconds().toString().padStart(2, "0")}`;

    return (
        <div className="clock">
            <select
                className="clock-selector"
                value={isDigital ? "digital" : "analog"}
                onChange={(e) => setIsDigital(e.target.value === "digital")}
            >
                <option value="analog">Analog</option>
                <option value="digital">Digital</option>
            </select>
            {isDigital ? (
                <div className="digital-clock">{formatTime(time)}</div>
            ) : (
                <div className="analog-clock">
                    <div
                        className="hour-hand"
                        style={{ transform: `rotate(${(time.getHours() % 12) * 30}deg)` }}
                    />
                    <div
                        className="minute-hand"
                        style={{ transform: `rotate(${time.getMinutes() * 6}deg)` }}
                    />
                    <div
                        className="second-hand"
                        style={{ transform: `rotate(${time.getSeconds() * 6}deg)` }}
                    />
                    <div className="center-dot" />
                </div>
            )}
        </div>
    );
}

export default Clock;
